/*Data for the table `teachers` */

insert  into `teachers`(`id`,`name`,`desc`,`is_in_school`,`is_in_club`,`created_at`,`updated_at`,`logo_file_name`,`logo_content_type`,`logo_file_size`,`logo_updated_at`) values (1,'ergwergwregerwgrege','ergfergrewgre',1,1,'2015-11-09 21:40:05','2015-11-11 15:36:07','foto.jpg','image/jpeg',34156,'2015-11-11 15:36:07'),(2,'Надійка Микуляк','Я люблю дуже багато речей, люблю любити, люблю мир і спокій, дуже люблю співати. Англійська постійно навколо мене, це те, в чому я зростаю і за допомогою чого пізнаю світ з іншої перспективи. А викладаю англійську тому, що люблю ділитись тим, що знаю і що маю, люблю, коли інші можуть зазирнути в нові обрії та поринути в нові глибини.',1,1,'2015-11-09 21:43:37','2016-01-12 19:59:22','Nadia_photo.jpg','image/jpeg',32658,'2016-01-12 19:55:12'),(3,'Іванка Клименко','Люблю навчати англійської дорослих людей, які знають чого хочуть. Надихаюсь мандрівками та іншим якісним часом.',1,1,'2015-11-25 17:59:19','2015-11-25 18:01:30','rbmokWTmwQ0.jpg','image/jpeg',28987,'2015-11-25 18:01:30'),(4,'Ірина Дзвінник','Культуролог за освітою, викладач англійської мови за покликанням. \nЛюблю вільно-творчо-індвідуальний підхід та процес обміну ідеями.\nНадихає: література, кінокультура, юнгіанська психологія, цікаві місця та незвичайні люди (і навпаки).',1,1,'2016-01-12 20:02:04','2016-01-12 20:02:26','Ira_photo.jpg','image/jpeg',37189,'2016-01-12 20:02:26');

